new-vhd -path (read-host "Path") -size (1 * (read-host "Size"))|mount-vhd -passthru|get-disk -number {$_.DiskNumber}|Initialize-Disk -Passthru|New-Partition -usemaximumsize -assigndriveletter:$false|get-partition|Add-PartitionAccessPath -AssignDriveLetter -PassThru|get-volume|format-volume -Confirm:$false -Force|foreach-object {Enable-BitLocker $_.DriveLetter -Password (read-host "Password" -AsSecureString) -PasswordProtector -EncryptionMethod Aes256 -UsedSpaceOnly}
